foreach(MemoryStream aStream in outBuffer)
{
	FileStream outStream = new FileStream("oneByone" + fileNum.ToString() + ".pdf",FileMode.Create);
	aStream.WriteTo(outStream);
	outStream.Close();
	fileNum++;
}

