[C#]

 //Create a PdfXmpMetadata object like this:
	PdfXmpMetadata xmpMetadata = new PdfXmpMetadata();
	//The input pdf stream.
	FileStream input = new FileStream(KenTestPath + "PdfXmpMetadata.AddXmpRdf.in.pdf",FileMode.Open);
	//The output pdf stream.
	FileStream output = new FileStream(KenTestPath + "PdfXmpMetadata.AddXmpRdf.out.pdf",FileMode.Create);
	xmpMetadata.InputStream = input;
	xmpMetadata.OutputStream = output;

	//A XmpSchema which is a part of resource discription format (rdf).
	XmpSchema xmp = new XmpSchema();

	//Add a user defined property into the XmpSchema.
	string val = @"my metadata heremore metadata here";
	string name = "pdf:MyMetadataName";
	xmp.AddUserProperty(name, val);

	//Add another user defined property into the XmpSchema.
	name = "pdf:Another";
	val = @"MyTag3 data";
	xmp.AddUserProperty(name, val);

	//Add a rdf into the XmpMetadata. Multiple rdfs can be added.
	xmpMetadata.AddXmpRdf(xmp);
	//Save the change.
	xmpMetadata.Save();

	//Close the input and output stream.
	input.Close();
	output.Close();

	[Visual Basic]

	'Create a PdfXmpMetadata object like this:
	Dim xmpMetadata As PdfXmpMetadata =  New PdfXmpMetadata() 
	'The input pdf stream.
	Dim input As FileStream =  New FileStream(KenTestPath + "PdfXmpMetadata.AddXmpRdf.in.pdf",FileMode.Open) 
	'The output pdf stream.
	Dim output As FileStream =  New FileStream(KenTestPath + "PdfXmpMetadata.AddXmpRdf.out.pdf",FileMode.Create) 
	xmpMetadata.InputStream = input
	xmpMetadata.OutputStream = output

	'A XmpSchema which is a part of resource discription format (rdf).
	Dim xmp As XmpSchema =  New XmpSchema() 

	'Add a user defined property into the XmpSchema.
	Dim val As String =  "my metadata heremore metadata here" 
	Dim name As String =  "pdf:MyMetadataName" 
	xmp.AddUserProperty(name, val)

	'Add another user defined property into the XmpSchema.
	name = "pdf:Another"
	val = "MyTag3 data"
	xmp.AddUserProperty(name, val)

	'Add a rdf into the XmpMetadata. Multiple rdfs can be added.
	xmpMetadata.AddXmpRdf(xmp)
	'Save the change.
	xmpMetadata.Save()

	'Close the input and output stream.
	input.Close()
	output.Close()

