example.code:
[C#] Pdf pdf1 = new Pdf(); Section sec1 = pdf1.Sections.Add();
Graph graph1 = new Graph(sec1,100,400); sec1.Paragraphs.Add(graph1); Arc arc1 = new Arc(graph1,200,50,30,30,90); graph1.Shapes.Add(arc1);
arc1.GraphInfo.Color = new Aspose.Pdf.Color("Red");
pdf1.Save(...);
[Visual Basic] Dim pdf1 As Pdf = New Pdf() Dim sec1 As Section = pdf1.Sections.Add()
Dim graph1 As Graph = New Graph(sec1, 100, 400) sec1.Paragraphs.Add(graph1) Dim arc1 As Arc = New Arc(graph1, 200, 50, 30, 30, 90) graph1.Shapes.Add(arc1)
arc1.GraphInfo.Color = New Aspose.Pdf.Color("Red")
pdf1.Save(...)
[XML] <?xml version="1.0" encoding="utf-8" ?> <Pdf xmlns="Aspose.Pdf"> <Section> <Graph Height="100" Width="400"> <Arc CenterPosition="200 50" Radius="30" Alpha="30" Beta="90" Color="Red"> </Arc> </Graph> </Section> </Pdf>

[C#]
Pdf pdf1 = new Pdf();

Section sec1 = pdf1.Sections.Add();

Attachment fileAttachment = new Attachment();
sec1.Paragraphs.Add(fileAttachment);
fileAttachment.AttachmentType = AttachmentType.File;
fileAttachment.AttachedFileName = "c:/images/ccitt.tif";
fileAttachment.AttachedFileType = "tif";	
fileAttachment.FileIconType = FileIconType.Graph;
fileAttachment.IconColor = new Aspose.Pdf.Color("Brown");

Attachment noteAttachment = new Attachment();
sec1.Paragraphs.Add(noteAttachment);
noteAttachment.AttachmentType = AttachmentType.Note;
noteAttachment.NoteContent = "This is a note.";
noteAttachment.NoteHeading = "this is a heading";
noteAttachment.IsNoteOpen = true;	

pdf1.Save("test.pdf");

[Visual Basic] 
Dim pdf1 As Pdf = New Pdf()

Dim sec1 As Section = pdf1.Sections.Add()

Dim fileAttachment As Attachment = New Attachment()
sec1.Paragraphs.Add(fileAttachment)
fileAttachment.AttachmentType = AttachmentType.File
fileAttachment.AttachedFileName = "c:/images/ccitt.tif"
fileAttachment.AttachedFileType = "tif"
fileAttachment.FileIconType = FileIconType.Graph
fileAttachment.IconColor = New Aspose.Pdf.Color("Brown")

Dim noteAttachment As Attachment = New Attachment()
sec1.Paragraphs.Add(noteAttachment)
noteAttachment.AttachmentType = AttachmentType.Note
noteAttachment.NoteContent = "This is a note."
noteAttachment.NoteHeading = "this is a heading"
noteAttachment.IsNoteOpen = True

pdf1.Save("test.pdf")

[XML]
<?xml version="1.0" encoding="utf-8" ?>
<Pdf xmlns="Aspose.Pdf">
	<Section>
		<Attachment AttachmentType="File" AttachedFileName="c:/images/flower.jpg"
			AttachedFileType="jpeg" FileIconType="Graph" IconColor="Brown">
		</Attachment>
		<Attachment AttachmentType="Note" IsNoteOpen="true" 
			NoteContent="This is a note.">
		</Attachment>
	</Section>
</Pdf>

[XML] <Text> <TextBorder> <All LineWidth="0.5" BorderStyle="Double" /> </TextBorder> <Segment>hello world</Segment> </Text>
[C#] Text t = new Text("hello world"); t.TextInfo.TextBorder = new BorderInfo((int)BorderSide.All,0.5F); t.TextInfo.TextBorder.SetBorderStyle((int)BorderSide.All,BorderStyle.Double);
[VB.NET] Dim t As Text = New Text("hello world") t.TextInfo.TextBorder = New BorderInfo(CType(BorderSide.All, Integer),0.5F) t.TextInfo.TextBorder.SetBorderStyle(CType(BorderSide.All, Integer),BorderStyle.Double)

[C#]	
Aspose.Pdf.Pdf pdf = new Aspose.Pdf.Pdf(0;
Aspose.Pdf.Section sec = new Aspose.Pdf.Section();
pdf.Sections.Add(sec);
Aspose.Pdf.Canvas canvas = new Aspose.Pdf.Canvas();
canvas.FillColor = new Aspose.Pdf.Color(0, 128, 128);
sec.Paragraphs.Add(canvas);
Aspose.Pdf.FloatingBox fb1 = new Aspose.Pdf.FloatingBox();
fb1.Left = 250F;
fb1.Top = 150F;
fb1.Paragraphs.Add(new Aspose.Pdf.Text("This is a text in floating box."));
canvas.Paragraphs.Add(fb1);
Aspose.Pdf.FloatingBox fb2 = new Aspose.Pdf.FloatingBox();
fb2.Left = 50F;
fb2.Top = 100F;
Aspose.Pdf.Image image2 = new Aspose.Pdf.Image();
image2.ImageInfo.File = "temp.jpeg";
image2.ImageInfo.ImageFileType = Aspose.Pdf.ImageFileType.Jpeg;
fb2.Paragraphs.Add(image2);
canvas.Paragraphs.Add(fb2);
pdf.Save(...);

[Visual Basic]
Dim pdf1 As Pdf = New Pdf()
Dim sec1 As Section = pdf1.Sections.Add()
pdf1.Sections.Add(sec1)
Dim canvas As Canvas = New Canvas()
canvas.FillColor = new Aspose.Pdf.Color(0, 128, 128)
sec1.Paragraphs.Add(canvas)
Dim fb1 As FloatingBox = New FloatingBox()
fb1.Left = 250F
fb1.Top = 150F
fb1.Paragraphs.Add(new Aspose.Pdf.Text("This is a text in floating box."))
canvas.Paragraphs.Add(fb1)
Dim fb2 As FloatingBox = New FloatingBox()
fb2.Left = 50F
fb2.Top = 100F
Dim image2 = New Image()
image2.ImageInfo.File = "temp.jpeg"
image2.ImageInfo.ImageFileType = Aspose.Pdf.ImageFileType.Jpeg;
fb2.Paragraphs.Add(image2)
canvas.Paragraphs.Add(fb2)
pdf1.Save(...)

[XML]
<Pdf xmlns="Aspose.Pdf">
	<Section>
		<Canvas  Width="450" Height="440">
			<Border>
				<Box LineWidth="0.5">
				</Box>
			</Border>
			<FloatingBox Left="250" Top="150">
				<Text>
					<Segment>This is a text in floating box.
					</Segment>
				</Text>
			</FloatingBox>
			<FloatingBox Left="50" Top="100">
				<Image File="F:/temp/temp.002.jpeg" Type="Jpeg">
				</Image>
			</FloatingBox>
		</Canvas>
		<Text>
			<Segment>ADFASFDASDF
			</Segment>
		</Text>
	</Section>
</Pdf>

