 [XML]
<?xml version="1.0" encoding="utf-8" ?> 
 <Pdf xmlns="Aspose.Pdf">
   <DocumentAttachment DocumentAttachmentFileName="c:/word.doc" 
       DocumentAttachmentFileType="doc">
   </DocumentAttachment>
   <DocumentAttachment DocumentAttachmentFileName="c:/image.jpg" 
       DocumentAttachmentFileType="jpeg">
   </DocumentAttachment>  
   <Section>
     <Attachment AttachmentType="File" AttachedFileName="c:/text.txt" AttachedFileType="txt" 
     		FileIconType="Graph" IconColor="Brown">
     </Attachment>
     <Attachment AttachmentType="File" AttachedFileName="c:/readme.txt" AttachedFileType="txt" 
     		FileIconType="PaperClip">
     </Attachment>
     <Attachment AttachmentType="Note" IsNoteOpen="true" 
     		NoteContent="This is a note.">
     </Attachment>
   </Section>
 </Pdf>

