Pdf pdf1 = new Pdf();

Section sec1 = pdf1.Sections.Add();
Segment seg1 = new Segment("dog");
EndNote endnote1 = new EndNote("dog is an animal");
seg1.EndNote = endnote1;

Text text1 = new Text();
text1.Segments.Add(seg1);

sec1.Paragraphs.Add(text1);

pdf1.Save(...);

