<?xml version="1.0" encoding="utf-8"?>
<Pdf>
	<ListSection ListType="TableOfContents">
		<Title Alignment="Center">
			<Segment IsTrueTypeFontBold="true" FontSize="30"> TableOfContents
			</Segment>
		</Title>
		<ListLevelFormat Level="1" LeftMargin="0">
			<TextInfo IsTrueTypeFontBold="true" IsTrueTypeFontItalic="true">
			</TextInfo>
		</ListLevelFormat>
		<ListLevelFormat Level="2" LeftMargin="10">
			<TextInfo IsUnderline="true" FontSize="10">
			</TextInfo>
		</ListLevelFormat>
		<ListLevelFormat Level="3" LeftMargin="20">
			<TextInfo IsTrueTypeFontBold="true">
			</TextInfo>
		</ListLevelFormat>
		<ListLevelFormat Level="4" LeftMargin="30">
			<TextInfo IsTrueTypeFontBold="true">
			</TextInfo>
		</ListLevelFormat>
	</ListSection>
	<ListSection ListType="ListOfTables">
		<Title>
			<Segment IsTrueTypeFontBold="true" FontSize="30">ListOfTables</Segment>
		</Title>
	</ListSection>
	<ListSection ListType="ListOfFigures">
		<Title>
			<Segment IsTrueTypeFontBold="true" FontSize="30">ListOfFigures</Segment>
		</Title>
	</ListSection>
	<Section>
		<Heading level="1" IsAutoSequence="true" IsInList="true">
			<Segment>this is heading of level 1</Segment>
		</Heading>
		<Heading level="2" IsAutoSequence="true" IsInList="true">
			<Segment>this is heading of level 2</Segment>
		</Heading>
		<Heading level="3" IsAutoSequence="true" IsInList="true">
			<Segment>this is heading of level 3</Segment>
		</Heading>
		<Heading level="4" IsAutoSequence="true" IsInList="true">
			<Segment>this is heading of level 4</Segment>
		</Heading>
		<Table ColumnWidths="70 2cm" IsInList="true">
			<Row>
				<Cell ColumnsSpan="2">
					<Border>
						<All LineWidth="0.5"/>
					</Border>
					<Text>
						<Segment>ColumnsSpan = 2</Segment>
					</Text>
				</Cell>
			</Row>
			<Row>
				<Cell>
					<Border>
						<All LineWidth="0.5"/>
					</Border>
					<Text>
						<Segment>cell1</Segment>
					</Text>
				</Cell>
				<Cell>
					<Border>
						<All LineWidth="0.5" Color="Red"/>
					</Border>
					<Text>
						<Segment>cell2</Segment>
					</Text>
				</Cell>
			</Row>
		</Table>
		<Image File="C:/118528527.jpg" Type="jpeg" IsInList="true">
			<Note Position="50 100">this is note1</Note>
			<Title>mm picture</Title>
		</Image>
		<Graph Height="100" Width="400" IsInList="true">
			<Curve Position="0 0 200 80 300 40 350 90"/>
			<Title>one curve</Title>
		</Graph>
	</Section>
</Pdf>

